import React from 'react';
import backImage from '../food_photos/food_page_background.jpg';
import {FoodCardGrid} from "../components/infocard.js";
import {PageNav} from '../components/pageNav.js';
import '../App.css';

class food extends React.Component {
  state = {
    elements: [

    ],

    page: 0,

    link: '/FoodPage'
  }

  incrementPage = () => {
    const page = this.state.page + 1;
    this.setState({page});
  }

  decrementPage = () => {
    const page = this.state.page - 1;
    this.setState({page});
  }

  goFirstPage = () => {
    const page = 0;
    this.setState({page});
  }

  goLastPage = () => {
    const page = this.state.elements.length/9 - 1;
    this.setState({page});
  }

  styles = {
    image: {
      width: 200,
      height: 150
    },

    background:{
      backgroundImage: `url(${backImage}`,
      height: '220vh', // 220 vh
      width: '100vw',
      overflow: 'auto',
      backgroundRepeat: 'no-repeat',
      backgroundPosition: 'center',
      backgroundSize: 'cover',
      backgroundAttachment: 'fixed'
    },

    header: {
      textAlign: 'center',
      color: 'aliceblue',
      fontSize: 150
    },

    text: {
      textAlign: 'center',
      color:'aliceblue',
      fontSize: 30
    }
  };

  render() {
    return (


      <div className="img-fluid" style={this.styles.background}>
        <h1 class="display-1 mb-4" style={this.styles.header}>Food ({this.state.elements.length})</h1>

        <FoodCardGrid link={this.state.link} elements={this.state.elements} currentPage={this.state.page}/>
        <PageNav label='Food Page Navigator' page={this.state.page} decrementPage = {this.decrementPage}
         incrementPage = {this.incrementPage} lastPage={this.state.elements.length/9} goFirstPage = {this.goFirstPage} goLastPage = {this.goLastPage}/>
         <p style={this.styles.text}>of {this.state.elements.length/9}</p>

      </div> 
    )
  }
}

export default food
