import React, { Component } from 'react';
import {Tabs, Tab} from "react-bootstrap";
import * as d3 from 'd3';
import BarChart from '../components/BarChart'


export class Visualizations extends Component
{
  constructor(props)
  {
    super();
    this.state = {
        activeTab: "foodKey",
        foodData: [],
        foodBarDataLoaded: false
    }
    this.handleSelect = this.handleSelect.bind(this);
  }

  handleSelect(selectedTab)
  {
    this.setState({
      activeTab: selectedTab
    });
  }

  // food
  foodBar ()
  {
    /*let foodDataNew = this.state.foodData;
      for (let i=0; i<this.state.foodData.length; i++){
        foodDataNew.push({label: this.state.foodData[i]['name'], value: this.state.foodData[i]['calories']});
      }
      console.log(foodDataNew)*/
    return (
      <div>
          {this.state.foodData}
        <div className="pageBox" align="center">
          <br />
          <h2><b>Food</b></h2>
          <br />
          <BarChart data = {foodDataNew}  isLoaded = {this.state.foodBarDataLoaded}  x = "Food" y = "Calories" label = "FoodChart"/>
          <br />
        </div>
      </div>
    );
  }

  async componentDidMount() 
  {
    let url = 'https://cors-anywhere.herokuapp.com/http://api.foodforthoughtt.me/food';

    fetch(url)
    .then(response => response.json())
    .then(data => {
      let foodDataNew = this.state.foodData;
      for (let i=0; i<this.state.foodData.length; i++){
        foodDataNew.push({label: this.state.foodData[i]['name'], value: this.state.foodData[i]['calories']});
      }
      console.log(foodDataNew)
      this.setState({
          foodData: foodDataNew,
          foodBarDataLoaded : true});
    })
    console.log(this.state.foodData)
  }




  render (){
    return (
        <Tabs className="tab" activeKey={this.state.activeTab} onSelect={this.handleSelect}>
            <Tab className="tabBackground" eventKey="foodKey" title="Food title">
            <br/>
                {this.foodBar()}
            <br/>
            </Tab>
        </Tabs>
    );
  }
}
