import React, { Component } from 'react';
import * as d3 from "d3";
import d3Tip  from "d3-tip";





//This code is modified from Simple d3.js bar chart D3: http://bl.ocks.org/d3noob/8952219#bar-data.csv
//with rainbow function from this codepen: https://codepen.io/chadoh/pen/NRXpKw

class BarChart extends Component
{
  constructor(props) {
    super(props);
    this.state =
    {
      isLoaded: false,
      bottomText: "Loading...",
    };
  }

  drawBarGraph()
  {
    var svgWidth = 500;  
    var svgHeight = 300;

    var svg = d3.select('svg')  
        .attr("width", svgWidth)  
        .attr("height", svgHeight)  
        .attr("class", "bar-chart");
}

  componentDidMount()
  {
    if (this.props.isLoaded && !this.state.isLoaded)
    {
      this.drawBarGraph();
      this.setState({
        isLoaded : true,
        bottomText: this.props.x,
      });
    }
  }

  getCustomTag ()
  {
    if (this.props.label === "FoodChart")
    {
      return this.drawBarGraph();
    }
  }



  render()
  {
    return (
      <div>
          {this.getCustomTag()}
      <h2>{this.state.bottomText}</h2>
        <br/>
      </div>
    );
  }
}

export default BarChart;
