import React, { Component } from 'react';
import {Text} from 'react-native';
import {NavLink} from 'react-router-dom';

export class FoodCard extends Component {
    state = { 
        style: {
        height: 250
        }
    }

    render() { 
        var dataPoints = [5];
        dataPoints[0] = this.formatData(this.props.info.point1)
        dataPoints[1] = this.formatData(this.props.info.point2)
        dataPoints[2] = this.formatData(this.props.info.point3)
        dataPoints[3] = this.formatData(this.props.info.point4)
        dataPoints[4] = this.formatData(this.props.info.point5)
        return ( 
        <div class="card bg-light">
            <div class="card-header font-weight-bold">{this.props.info.header}</div>
            <img class="card-img-top" src={this.props.info.image} style={this.state.style} alt/>
            <div class="card-body">
                <div class="list-group">
                    <div class="list-group-item"><Text style={{fontSize: 17, fontWeight: 'bold'}}>Protein:</Text>{this.props.info.point1}</div>
                    <div class="list-group-item"><Text style={{fontSize: 17, fontWeight: 'bold'}}>Fat:</Text>{this.props.info.point2}</div>
                    <div class="list-group-item"><Text style={{fontSize: 17, fontWeight: 'bold'}}>Carbs:</Text>{this.props.info.point3}</div>
                    <div class="list-group-item"><Text style={{fontSize: 17, fontWeight: 'bold'}}>Sodium:</Text>{this.props.info.point4}</div>
                    <div class="list-group-item"><Text style={{fontSize: 17, fontWeight: 'bold'}}>Calories:</Text>{this.props.info.point5}</div>
                </div>
                <div class="card-text"><p></p></div>
                <NavLink to={{pathname: this.props.link, state: {index: this.props.index}}} class="card-link">More Info</NavLink>
            </div>
        </div> 
        );
    }
}

