import React, { Component } from 'react';
import * as d3 from "d3";
import d3Tip  from "d3-tip";



//This code is modified from Simple d3.js bar chart D3: http://bl.ocks.org/d3noob/8952219#bar-data.csv
//with rainbow function from this codepen: https://codepen.io/chadoh/pen/NRXpKw

class BarChart extends Component
{
  constructor(props) {
    super(props);
    this.state =
    {
      isLoaded: false,
      bottomText: "Loading...",
    };
  }

  componentDidMount() {
    var arr = new Array();
    // call API
    let url = 'https://cors-anywhere.herokuapp.com/http://api.foodforthoughtt.me/food';
    fetch(url)
    .then(response => response.json())
    .then(data => {
      for (let i=0; i < data.length; i++) {
        const it = parseInt(data[i]['calories']);
        arr.push(it)
      }
    })
    console.log(arr)
    //arr = []
    arr = [ 50, 56, 43, 53, 52, 47, 39, 160, 43, 534, 208, 144, 97, 133,
    34, 49, 32, 23, 13, 23, 25, 59, 402, 86, 155, 567, 654, 562, 26, 18, 68, 347, 
  81, 368, 189, 23 ]
    console.log(arr);
    this.drawBarChart(arr);
  }

  drawBarChart(data)  {
    const margin = {top: 30, right: 20, bottom: 70, left: 50},
      width = 1200 - margin.left - margin.right,
      height = 800 - margin.top - margin.bottom;
    const canvasHeight = 800
    const canvasWidth = 1118
    const scale = 1
    var xAxis = d3.svg.axis().scale(x)
    const svgCanvas = d3.select(this.refs.canvas)
        .append("svg")
          .attr("width", width + margin.left + margin.right)
          .attr("height", height + margin.top + margin.bottom)
        .append("g")
          .attr("tranform", "translate(" + margin.left + "," + margin.top + ")")
        .style("border", "1px solid black");
    svgCanvas.selectAll("rect")
        .data(data).enter()
            .append("rect")
            .attr("width", 30)
            .attr("height", (datapoint) => datapoint * scale)
            .attr("fill", "orange")
            .attr("x", (datapoint, iteration) => iteration * 31)
            .attr("y", (datapoint) => height - datapoint * scale)

    svgCanvas.selectAll("text")
      .data(data).enter()
          .append("text")
          .attr("x", (dataPoint, i) => i * 31 + 2)
          .attr("y", (dataPoint, i) => height - dataPoint*scale)
          .attr("transform", d => "rotate(-90)")
          .text('example label lol ')
    svgCanvas.append("g")
      .attr("class", "x axis")
      .attr("transform", "translate(0," + height + ")")
      .call(xAxis)
      .selectAll("text")
        .style("text-anchor", "end")
        .attr("dx", "-.8em")
        .attr("dy", ".15em")
        .attr("transform", function(d) {return "rotate(-65)"});

  }

  render() { 
    return <div ref="canvas"></div> 
  }
}

export default BarChart;
