import React, { Component } from 'react';
import {NavLink} from "react-router-dom";
import backImage from '../food_photos/food_page_background.jpg'
import {Link} from 'react-router-dom';

export class FoodPage extends Component {
    state = { 
        elements: [

        ],
        index: 0
    }

    styles = {
        image: {
          width: 401,
          height: 300
        },

        background:{
          backgroundImage: `url(${backImage}`,
          height: '100vh', // 220 vh
          width: '100vw',
          overflow: 'hidden',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
          backgroundAttachment: 'fixed'
        },

        header: {
          textAlign: 'left',
          color: 'aliceblue',
          fontSize: 100
        },

        text: {
            color: 'aliceblue',
            fontSize: 30
        },

        link: {
            color: 'orange',
            fontSize: 50
        }
    };

    componentDidMount () {
        let url = 'https://cors-anywhere.herokuapp.com/http://api.foodforthoughtt.me/food';
        console.log("first");
        fetch(url)
        .then(response => response.json())
        .then(data => {
        console.log(data);
        const elements = data;
        this.setState({elements});
        })
        const {index} = this.props.location.state;
        this.setState({index});
    }
    render() { 
        return ( 
            <div className="img-fluid" style={this.styles.background}>
                <h1 class="display-1 ml-4" style={this.styles.header}>&nbsp;{this.state.elements[this.state.index].name}</h1>
                <div class="row text-left ml-5 mt-5">
                    <img class="img-thumbnail ml-4" src = {this.state.elements[this.state.index].images} style = {this.styles.image}/>
                    <div class="col">
                        <div class="row">
                            <p class="ml-5 font-weight-bold lead" style={this.styles.text}>Protein:</p>
                            <p class="ml-3 lead" style={this.styles.text}>{this.state.elements[this.state.index].protein}</p>
                        </div>
                        <div class="row">
                            <p class="ml-5 font-weight-bold lead" style={this.styles.text}>Fat:</p>
                            <p class="ml-3 lead" style={this.styles.text}>{this.state.elements[this.state.index].fat}</p>
                        </div>
                        <div class="row">
                            <p class="ml-5 font-weight-bold lead" style={this.styles.text}>Carbs:</p>
                            <p class="ml-3 lead" style={this.styles.text}>{this.state.elements[this.state.index].carbs}</p>
                        </div>
                        <div class="row">
                            <p class="ml-5 font-weight-bold lead" style={this.styles.text}>Sodium:</p>
                            <p class="ml-3 lead" style={this.styles.text}>{this.state.elements[this.state.index].sodium}</p>
                        </div>
                        <div class="row">
                            <p class="ml-5 font-weight-bold lead" style={this.styles.text}>Calories:</p>
                            <p class="ml-3 lead" style={this.styles.text}>{this.state.elements[this.state.index].calories}</p>
                        </div>
                    </div>
                    <div class="col text-right mr-5">
                        <Link to='/' style={this.styles.link}>Example Link 1 <br/></Link>
                        <Link to='/Food' style={this.styles.link}>Example Link 2</Link>
                    </div>
                </div>
            </div>    
        );
    }
}
