      let url = 'https://cors-anywhere.herokuapp.com/http://api.foodforthoughtt.me/' + pages[idx];
      fetch(url)
      .then(response => response.json())
      .then(data => {
        console.log(data);
        this.setState({elements: data});
      })*/

        console.log(this.state.searchParams);
