  componentDidMount()
  {
    if (this.props.isLoaded && !this.state.isLoaded)
    {
      this.drawBarGraph();
      this.setState({
        isLoaded : true,
        bottomText: this.props.x,
      });
    }
  }



  render()
  {
    return (
      <div>
      <h2>{this.state.bottomText}</h2>
        <br/>
      </div>
    );
  }
}

export default BarChart;
