import React, { Component } from 'react';
import { Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button, CardHeader } from 'reactstrap';
import { Container, Row, Col } from 'reactstrap';

export class InfoCard extends Component {
    state = {  }
    render() { 
        return ( <Card>
            <CardHeader>{this.props.info.header}</CardHeader>
            <CardBody>
                <CardText>
                    {this.props.info.text}
                </CardText>
            </CardBody>
        </Card> );
    }
}
 
export class InfoCardGrid extends Component {
    state = { 
        elements: [
            {header: 'Blueberries', text: 'it\'s blue!', point1: 'data point 1',
            point2: 'data point 2', point3: 'data point 3', point4: 'data point 4',
            point5: 'data point 5'},
            {header: 'Seaweed', text: 'its a plant', point1: 'data point 1',
            point2: 'data point 2', point3: 'data point 3', point4: 'data point 4',
            point5: 'data point 5'}
        ]
     }
    render() { 
        return ( <Container>
            <Row>
                <Col> <InfoCard info = {this.state.elements[0]} /></Col>
                <Col> <InfoCard info = {this.state.elements[1]} /></Col>
            </Row>
        </Container> );
    }
}
