import React, { Component } from 'react';
import {Tabs, Tab} from "react-bootstrap";
import * as d3 from 'd3';
import BarChart from '../components/BarChart'

export class Visualizations extends Component
{
  constructor(props)
  {
    super();
    this.state = {
        activeTab: "foodKey",
        foodData: [],
    }
    this.handleSelect = this.handleSelect.bind(this);
  }

  handleSelect(selectedTab)
  {
    this.setState({
      activeTab: selectedTab
    });
  }

  async componentDidMount() {
    try {
      // call API
      let url = 'https://cors-anywhere.herokuapp.com/http://api.foodforthoughtt.me/food';
      const response = await fetch(url);
      // wait for response
      if (!response.ok) {
        throw Error(response.statusText);
      }
      const json = await response.json();
      this.setState({ data: json });
    } catch (error) {
      console.log(error);
    }
  }

  // food
  foodBar ()
  {
    var labels = []
    var values = []
    for (let i=0; i < this.state.data.length; i++) {
      labels.push(this.state.data[i]['name'])
      values.push(this.state.data[i]['calories'])
    }
    this.setState({foodData: values});

    return (
      <div>
        <div className="pageBox" align="center">
          <br />
          <h2><b>Food</b></h2>
          <br />
            <BarChart data={this.state.foodData}/>
          <br />
        </div>
      </div>
    );
  }

  render (){
    console.log(this.state.foodData)
    return (
        <Tabs className="tab" activeKey={this.state.activeTab} onSelect={this.handleSelect}>
            <Tab className="tabBackground" eventKey="foodKey" title="Food title">
            <br/>
                {/* <BarChart /> */}
                {this.foodBar()}
            <br/>
            </Tab>
        </Tabs>
    );
  }
}
